/* Vuex Library*/

import Vue from 'vue'
import Vuex from 'vuex'
import {auth} from '../firebase/config'

Vue.use(Vuex)


/* Firebase Library */
import {getFirestore,collection,onSnapshot,query/*,orderBy*/,where,addDoc,deleteDoc,doc,setDoc,} from 'firebase/firestore'

const empty_user = {
  "user": {
    userMail: "",
    lastConection: "2014-06-25T00:00:00.000Z",
    matchCounter: [],
    matchUsers: [
      {
        starwars86: {
        matchAcept: true,
          messages: [
            {
              sentBy: "starwars86",
              dateTime: "2022-05-15T13:18:57.647Z",
              content: "hola como estay"
            },
            {
              sentBy: "adminPablo",
              dateTime: "2022-05-15T13:18:57.647Z",
              content: "biÃ©n y tu?"
            }
          ]
        }
      }
    ]
  }
}

export default new Vuex.Store({

  state: {
    myFirestore: null,
    mensajes: null,
    user: null,
  },


  getters: {
  },

  mutations: {
    setUser(state, payload) {
      state.user = payload.email
      console.log(`user state change ${state.user}`)
    },


    instanceFirestore(state, fs) {
      state.myFirestore = fs
    },

    getMessages(state, mesages) {
      state.mensajes = mesages
    }
  },
  actions: {


    async signUp(context, { email, password }) {

      console.log('firebase create user')

      const res = await createUserWithEmailAndPassword(auth, email, password)
      if (res) {
        empty_user.userMail = email
        await addDoc(collection(getFirestore(), "starchat"), empty_user);
        context.commit('setUser', res.user)
      }
      else {
        throw new Error('No se pudo completar el registro de usuario')
      }

    }
    ,

    async signIn(context, { email, password }) {

      console.log('firebase login user')

      const res = await signInWithEmailAndPassword(auth, email, password)
      if (res) {
        context.commit('setUser', res.user)
      }
      else {
        throw new Error('No se pudo completar el inicio de sesion de usuario')
      }

    },

    async signOut(context) {
      console.log('signout action')
      await signOut(auth)
      context.commit('setUser', {email: null})

    }


    ,
    async instanceFirestore({ commit }, fs) {
      commit("instanceFirestore", fs())
    },

    async getMessages({ commit }) {
      if (this.state.myFirestore) {
        const db = getFirestore();
 /*       const q = query(collection(db, "starchat"), orderBy("date"));  */
       const q = query(collection(db, "starchat"));  

        onSnapshot(q, (querySnapshot) => {
          const mensajes = [];
          this.mensajes = [];
          querySnapshot.forEach((doc) => {
            mensajes.push({ id: doc.id, data: doc.data() });
          });
          commit('getMessages', mensajes)
        });
      }
    },


    async getAnotherUsers({ commit },objeto) {

      if (this.state.myFirestore) {
        const db = getFirestore();
        console.log(objeto.email)
       const q = query(collection(db, "starchat"),where('userMail','!=',objeto.email));  
        onSnapshot(q, (querySnapshot) => {
          const mensajes = [];
          this.mensajes = [];
          querySnapshot.forEach((doc) => {
            console.log(doc.id, " => ", doc.data());
            mensajes.push({ data: doc.data().userMail});
          });
          commit('getMessages', mensajes)
        });
      }


    },

    async getMyMessages({ commit },objeto) {

      if (this.state.myFirestore) {
        const db = getFirestore();
        console.log(objeto.email)
       const q = query(collection(db, "starchat"),where('userMail','==',objeto.email));  
        onSnapshot(q, (querySnapshot) => {
          const mensajes = [];
          this.mensajes = [];
          querySnapshot.forEach((doc) => {
            console.log(doc.id, " => ", doc.data());
            mensajes.push({ data: doc.data()});
          });
          commit('getMessages', mensajes)
        });
      }


    },

    async addMessage(commit, objeto) {
      if (objeto != "") {
        console.log(objeto)
        const db = getFirestore();
        await addDoc(collection(db, "starchat"), objeto);
        commit
      }
    },
    async delMessage(commit, objeto) {
      if (objeto != "") {
        console.log(objeto)
        const db = getFirestore();
        await deleteDoc(doc(db, "starchat", objeto));
        commit
      }
    },

    async editMessage(commit, objeto) {
      if (objeto != "") {
        console.log(objeto)
        const db = getFirestore();
        await setDoc(doc(db, "starchat", objeto[0]), objeto[1]);
        commit
      }
    },

  },
  modules: {
  }
}
  )
