/* Vuex Library*/

import Vue from 'vue'
import Vuex from 'vuex'
import {auth} from '../firebase/config'
import {signInWithEmailAndPassword,onAuthStateChanged,createUserWithEmailAndPassword} from 'firebase/auth'
import {getFirestore,collection,onSnapshot,query,where,addDoc/*,orderByaddDoc,deleteDoc,doc,setDoc,*/} from 'firebase/firestore'

Vue.use(Vuex)
export default new Vuex.Store({
  state: {
    myFirestore: null,
    usuario: '',
    miData: '',
    otherUsers: '',
  },

  getters: {
  },

  mutations: {
    setUser(state, payload) {
      state.usuario = payload.email
      console.log(`user state change ${state.usuario}`)
    },
    setUserInfo(state, payload) {
      console.log(payload.data)
      state.miData = payload.data
      console.log(`user data loaded ${payload.data}`)
    },
    setOtherUsers(state, payload) {
      console.log(payload)
      state.otherUsers = payload
      console.log(`other users loaded as:  ${payload}`)
    },
    instanceFirestore(state, fs) {
      state.myFirestore = fs
    },
    },

  actions: {


    async instanceFirestore(context, fs) {
      console.log('iniciado firestore')
      context.commit("instanceFirestore", fs())
    },

    async getCurrentUser(context){

     await onAuthStateChanged(auth, (user) => {
        if (user) {
          context.commit('setUser', user)
        } else {
          context.commit('setUser', {email:null})
        }
    })

  },

    async signIn(context, { email, password }) {
      const res = await signInWithEmailAndPassword(auth, email, password)
      if (res) {
        context.commit('setUser', res.user)
      }
    },

    async signUp(context, { email, password }) {
      const res = await createUserWithEmailAndPassword(auth, email, password)
      if (res) {
        console.log(res.user.email)
        const newUser = {

          "user": {
            userMail: res.user.email,
            registerDate: `"${Date.now()}"`,
            lastConection: `"${Date.now()}"`,
            matchCounter: 100,
            matchUsers: [
              {
                starwars86: {
                matchAcept: true,
                }

              }

            ]
          }

        }

        /*await addDoc(collection(getFirestore(), "starchat"), empty_user);*/
        await addDoc(collection(getFirestore(), "starchat"), newUser);
        context.commit('setUser', res.user)

      }


    },
    async getMyData({commit}) {
          console.log(`se ha ejecutadoe el metodo getMyData`)
          console.log(`cargando datos del usuario ${this.state.usuario}`)
          if (this.state.myFirestore) {
            const db = getFirestore();
            const q =  await query(collection(db, "starchat"),where('userMail','==',this.state.usuario));  
            onSnapshot(q, (querySnapshot) => {
              const mensajes = [];
              this.mensajes = [];
              querySnapshot.forEach((doc) => {
                console.log(doc.id, " => ", doc.data());
                mensajes.push({ data: doc.data()});
              });

              console.log(mensajes[0])
              commit('setUserInfo', mensajes[0])

            });
          }

    },

    async getAnotherUsers({commit}) {

          if (this.state.myFirestore) {
            const db = getFirestore();
            console.log(`rescatando usuarios que no sean: ${this.state.usuario}`)
            const q = await query(collection(db, "starchat"),where('userMail','!=',this.state.usuario));  
            onSnapshot(q, (querySnapshot) => {
              const mensajes = [];
              this.mensajes = [];
              querySnapshot.forEach((doc) => {
                console.log(doc.id, " => ", doc.data());
                mensajes.push({ data: doc.data().userMail});
              });
              console.log(`usuarios rescatados son:`)
              console.log(mensajes)
              commit('setOtherUsers', mensajes)
            });
          }

         } 


  },
  modules: {
  }
}
  )
